mapper.select:
        select *,
        (
	        (
	        	select incDec from corona_sido
            	where 
	            date_format(createDt, '%Y-%m-%d') = str_to_date(#{date}, '%Y-%m-%d')
	        	and gubun=#{region}
				limit 1
	        ) - (
	        	select incDec from corona_sido
	        	where 
	        	date_format(createDt, '%Y-%m-%d') 
	        	= date_sub(str_to_date(#{date}, '%Y-%m-%d'), interval 1 day)
	        	and gubun=#{region}
				limit 1
	        )
        )as diff
        from corona_sido where date_format(createDt, '%Y-%m-%d') 
        = str_to_date(#{date}, '%Y-%m-%d')
        and gubun=#{region}
		limit 1
